const availableEnvironments = [
  {
    production: false,
    name: 'local'
  },
  {
    production: true,
    name: 'stage'
  },
  {
    production: true,
    name: 'sandbox'
  },
  {
    production: true,
    name: 'production'
  },
  {
    production: false,
    name: 'develop'
  },
];

export const environment = (environmentName) => availableEnvironments.find(
  (env) => env.name === environmentName
);
